import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Mail, Phone, Lock } from "lucide-react";
import { motion } from "framer-motion";

export default function SignInPage() {
  const navigate = useNavigate();
  const [useEmail, setUseEmail] = useState(true);
  const [formData, setFormData] = useState({ emailOrPhone: "", password: "" });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSignIn = async () => {
    setLoading(true);
    setError("");

    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          [useEmail ? "email" : "phone"]: formData.emailOrPhone,
          password: formData.password,
        }),
      });

      const data = await res.json();

      if (res.ok) {
        localStorage.setItem("token", data.token);
        navigate("/home");
      } else {
        setError(data.message || "Invalid credentials");
      }
    } catch (err) {
      console.error(err);
      setError("Something went wrong. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        fontFamily: "Poppins, sans-serif",
        backgroundColor: "#ffffff",
        padding: "20px",
      }}
    >
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        style={{
          maxWidth: "400px",
          width: "100%",
          background: "#fefefe",
          padding: "40px",
          borderRadius: "20px",
          boxShadow: "0px 10px 30px rgba(0,0,0,0.1)",
          textAlign: "center",
        }}
      >
        <h1 style={{ fontSize: "2rem", marginBottom: "10px", color: "#0077ff" }}>
          TravelGo
        </h1>
        <p style={{ fontSize: "1rem", color: "#555", marginBottom: "30px" }}>
          Travel with Tripzo ✈️
        </p>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginBottom: "20px",
            gap: "10px",
          }}
        >
          <button
            onClick={() => setUseEmail(true)}
            style={{
              padding: "8px 16px",
              borderRadius: "8px",
              border: "none",
              background: useEmail ? "#0077ff" : "#eee",
              color: useEmail ? "#fff" : "#555",
              cursor: "pointer",
            }}
          >
            Email
          </button>
          <button
            onClick={() => setUseEmail(false)}
            style={{
              padding: "8px 16px",
              borderRadius: "8px",
              border: "none",
              background: !useEmail ? "#0077ff" : "#eee",
              color: !useEmail ? "#fff" : "#555",
              cursor: "pointer",
            }}
          >
            Phone
          </button>
        </div>

        <div
          style={{
            display: "flex",
            alignItems: "center",
            border: "1px solid #eee",
            borderRadius: "10px",
            padding: "12px",
            marginBottom: "20px",
          }}
        >
          {useEmail ? (
            <Mail style={{ marginRight: "8px", color: "#aaa" }} />
          ) : (
            <Phone style={{ marginRight: "8px", color: "#aaa" }} />
          )}
          <input
            type={useEmail ? "email" : "tel"}
            name="emailOrPhone"
            value={formData.emailOrPhone}
            onChange={handleChange}
            placeholder={useEmail ? "Email" : "Phone Number"}
            style={{ flex: 1, outline: "none", fontSize: "1rem" }}
          />
        </div>

        <div
          style={{
            display: "flex",
            alignItems: "center",
            border: "1px solid #eee",
            borderRadius: "10px",
            padding: "12px",
            marginBottom: "20px",
          }}
        >
          <Lock style={{ marginRight: "8px", color: "#aaa" }} />
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="Password"
            style={{ flex: 1, outline: "none", fontSize: "1rem" }}
          />
        </div>

        {error && (
          <p style={{ color: "red", fontSize: "0.9rem", marginBottom: "10px" }}>
            {error}
          </p>
        )}

        <button
          onClick={handleSignIn}
          disabled={loading}
          style={{
            width: "100%",
            padding: "12px",
            borderRadius: "12px",
            border: "none",
            background: "linear-gradient(90deg,#0077ff,#00aaff)",
            color: "#fff",
            fontSize: "1rem",
            cursor: "pointer",
            opacity: loading ? 0.7 : 1,
          }}
        >
          {loading ? "Signing in..." : "Sign In"}
        </button>

        <p style={{ marginTop: "20px", fontSize: "0.9rem", color: "#555" }}>
          Don't have an account?{" "}
          <span
            onClick={() => navigate("/signup")}
            style={{ color: "#0077ff", cursor: "pointer" }}
          >
            Sign Up
          </span>
        </p>
      </motion.div>
    </div>
  );
}
