import React, { useEffect, useState } from "react";

export default function ItineraryPage() {
  const [itineraries, setItineraries] = useState([]);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("itineraries")) || [];
    setItineraries(saved);
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-xl font-semibold text-blue-600 mb-4">My Itineraries</h1>
      {itineraries.length === 0 ? (
        <p>No itineraries saved yet.</p>
      ) : (
        <div className="grid gap-4">
          {itineraries.map((item, index) => (
            <div key={index} className="p-4 border rounded shadow bg-white">
              <h2 className="font-semibold text-lg">{item.name}</h2>
              <p className="text-gray-600 text-sm">{item.destination}</p>
              <p>Interest: {item.interest}</p>
              <p>Days: {item.days}</p>
              <p>Budget: â‚¹{item.budget}</p>
              <p className="text-sm text-gray-500 mt-1">
                Best Time: {item.recommendations?.bestTime}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
