import React, { useState } from "react";
import axios from "axios";

export default function AIPlanner() {
  const [form, setForm] = useState({
    destination: "",
    interest: "",
    days: "",
    budget: "",
  });
  const [recommendations, setRecommendations] = useState("");
  //const [savedTrips, setSavedTrips] = useState([]);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const getRecommendations = async () => {
    const { data } = await axios.post("http://localhost:5001/api/ai/recommendations", form);
    setRecommendations(data.recommendations);
  };

  const saveItinerary = () => {
    const tripName = prompt("Enter a name for your itinerary:");
    if (!tripName) return;

    const newTrip = {
      id: Date.now(),
      name: tripName,
      destination: form.destination,
      recommendations,
    };

    // Simulate saving to Itinerary service (mock for MVP)
    const existing = JSON.parse(localStorage.getItem("savedTrips") || "[]");
    localStorage.setItem("savedTrips", JSON.stringify([...existing, newTrip]));
    alert("âœ… Itinerary saved successfully!");
  };

  return (
    <div className="p-5 max-w-2xl mx-auto text-gray-800">
      <h1 className="text-2xl font-semibold text-blue-600 mb-4">AI Trip Planner</h1>

      <div className="flex flex-col gap-3 mb-5">
        <input name="destination" placeholder="Destination" value={form.destination} onChange={handleChange} className="border p-2 rounded" />
        <input name="interest" placeholder="Interest (e.g., Beaches, Temples)" value={form.interest} onChange={handleChange} className="border p-2 rounded" />
        <input name="days" placeholder="Days" value={form.days} onChange={handleChange} className="border p-2 rounded" />
        <input name="budget" placeholder="Budget" value={form.budget} onChange={handleChange} className="border p-2 rounded" />
        <button onClick={getRecommendations} className="bg-blue-600 text-white py-2 rounded">Get Recommendations</button>
      </div>

      {recommendations && (
        <div className="bg-gray-50 border p-4 rounded-lg">
          <h2 className="text-lg font-medium mb-2">Recommendations</h2>
          <p>{recommendations}</p>
          <button
            onClick={saveItinerary}
            className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            ðŸ’¾ Save Itinerary
          </button>
        </div>
      )}
    </div>
  );
}
