import React, { useEffect, useState } from "react";

export default function ItineraryPage() {
  const [itineraries, setItineraries] = useState([]);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("itineraries")) || [];
    setItineraries(saved);
  }, []);

  return (
    <div className="p-6 bg-white min-h-screen">
      <h1 className="text-2xl font-bold text-blue-600 mb-6">My Itineraries</h1>

      {itineraries.length === 0 ? (
        <p>No itineraries saved yet.</p>
      ) : (
        <div className="grid gap-4 max-w-3xl mx-auto">
          {itineraries.map((item, index) => (
            <div key={index} className="p-4 border rounded-lg shadow-sm bg-gray-50">
              <h2 className="font-semibold text-lg text-blue-700 mb-1">{item.name}</h2>
              <p>üìç Destination: {item.destination}</p>
              <p>üéØ Interest: {item.interest || "All"}</p>
              <p>üóìÔ∏è Days: {item.days}</p>
              <p>üí∞ Budget: ‚Çπ{item.budget}</p>
              <p className="text-sm text-gray-600 mt-1">Best Time: {item.bestTime || "N/A"}</p>
              <p className="mt-2 text-gray-800">{item.recommendation || ""}</p>

              {/* Day-by-day itinerary */}
              {item.itinerary && item.itinerary.length > 0 && (
                <div className="mt-4">
                  <h3 className="font-semibold mb-2">üóìÔ∏è Itinerary Details:</h3>
                  <ul className="list-disc ml-5 text-gray-700">
                    {item.itinerary.map((day, dayIndex) => (
                      <li key={dayIndex} className="mb-2">
                        <strong>{day.day}:</strong>
                        {day.activities && day.activities.length > 0 ? (
                          <ul className="list-decimal ml-5">
                            {day.activities.map((activity, actIndex) => (
                              <li key={actIndex}>{activity}</li>
                            ))}
                          </ul>
                        ) : (
                          <p className="ml-5 text-gray-500">No activities listed.</p>
                        )}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
