// Only showing the phone input part for brevity
<input
  type={useEmail ? "email" : "tel"}
  name="emailOrPhone"
  value={formData.emailOrPhone}
  onChange={(e) => {
    // allow only digits for phone
    if (!useEmail) {
      const val = e.target.value.replace(/\D/g, "");
      if (val.length <= 10) setFormData({ ...formData, emailOrPhone: val });
    } else {
      setFormData({ ...formData, emailOrPhone: e.target.value });
    }
  }}
  placeholder={useEmail ? "Email" : "Phone Number"}
  style={{ flex: 1, outline: "none", fontSize: "1rem" }}
/>
