import React, { useState } from "react";
import axios from "axios";

export default function AIPlanner() {
  const [destination, setDestination] = useState("");
  const [interest, setInterest] = useState("");
  const [days, setDays] = useState("");
  const [budget, setBudget] = useState("");
  const [recommendations, setRecommendations] = useState(null);

  const getRecommendations = async () => {
    if (!destination) {
      alert("Please enter a destination!");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5001/api/ai/recommendations", {
        destination,
        interest,
        days,
        budget,
      });
      setRecommendations(response.data);
    } catch (error) {
      console.error("Error fetching recommendations:", error);
      alert("Failed to fetch recommendations.");
    }
  };

  const saveItinerary = () => {
    if (!recommendations) return alert("No itinerary to save yet.");

    const itineraryName = prompt("Enter a name for this itinerary:");
    if (!itineraryName) return;

    const newItinerary = {
      name: itineraryName,
      destination,
      interest,
      days,
      budget,
      recommendation: recommendations.recommendation,
      bestTime: recommendations.bestTime,
      createdAt: new Date().toISOString(),
    };

    const savedItineraries = JSON.parse(localStorage.getItem("itineraries")) || [];
    savedItineraries.push(newItinerary);
    localStorage.setItem("itineraries", JSON.stringify(savedItineraries));

    alert(`Itinerary "${itineraryName}" saved successfully!`);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-white p-6 text-center">
      {/* Title */}
      <h1 className="text-2xl sm:text-3xl font-bold mb-6 text-blue-600">
        AI Trip Planner
      </h1>

      {/* Input Fields */}
      <div className="flex flex-col gap-3 w-full max-w-md">
        <input
          type="text"
          placeholder="Destination (e.g. Goa)"
          className="border p-3 rounded-md text-sm shadow-sm"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
        />
        <input
          type="text"
          placeholder="Interest (e.g. Beaches, Parties)"
          className="border p-3 rounded-md text-sm shadow-sm"
          value={interest}
          onChange={(e) => setInterest(e.target.value)}
        />
        <input
          type="number"
          placeholder="Days (e.g. 5)"
          className="border p-3 rounded-md text-sm shadow-sm"
          value={days}
          onChange={(e) => setDays(e.target.value)}
        />
        <input
          type="number"
          placeholder="Budget (₹)"
          className="border p-3 rounded-md text-sm shadow-sm"
          value={budget}
          onChange={(e) => setBudget(e.target.value)}
        />
        <button
          onClick={getRecommendations}
          className="bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition"
        >
          Get Recommendations
        </button>
      </div>

      {/* Recommendation Result */}
      {recommendations && (
        <div className="mt-8 bg-gray-50 p-5 rounded-lg shadow-md w-full max-w-md text-left">
          <h2 className="text-lg font-semibold mb-2 text-blue-600">
            Recommendations
          </h2>
          <p className="text-gray-700">
            <strong>Destination:</strong> {destination}
          </p>
          <p className="text-gray-700">
            <strong>Interest:</strong> {interest || "All"}
          </p>
          <p className="text-gray-700">
            <strong>Details:</strong> {recommendations.recommendation}
          </p>
          <p className="text-gray-500 text-sm mt-1">
            <strong>Best Time:</strong> {recommendations.bestTime}
          </p>

          <button
            onClick={saveItinerary}
            className="mt-4 bg-green-600 text-white py-2 px-4 rounded-md hover:bg-green-700 transition"
          >
            💾 Save Itinerary
          </button>
        </div>
      )}
    </div>
  );
}
