import React, { useEffect, useState } from "react";

export default function MyTripsPage() {
  const [trips, setTrips] = useState([]);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("savedTrips") || "[]");
    setTrips(saved);
  }, []);

  const deleteTrip = (id) => {
    const updated = trips.filter((t) => t.id !== id);
    localStorage.setItem("savedTrips", JSON.stringify(updated));
    setTrips(updated);
  };

  return (
    <div className="p-5 max-w-2xl mx-auto">
      <h1 className="text-2xl font-semibold text-blue-600 mb-4">My Trips</h1>
      {trips.length === 0 ? (
        <p className="text-gray-500">No saved trips yet.</p>
      ) : (
        trips.map((trip) => (
          <div key={trip.id} className="border p-4 rounded-lg mb-3 bg-gray-50">
            <h2 className="text-lg font-medium">{trip.name}</h2>
            <p className="text-sm text-gray-600 mb-2">
              Destination: <span className="font-medium">{trip.destination}</span>
            </p>
            <p className="text-sm">{trip.recommendations}</p>
            <button
              onClick={() => deleteTrip(trip.id)}
              className="mt-3 text-red-500 hover:text-red-700 text-sm"
            >
              üóëÔ∏è Delete
            </button>
          </div>
        ))
      )}
    </div>
  );
}
