import React, { useEffect, useState } from "react";

export default function MyTripsPage() {
  const [itineraries, setItineraries] = useState([]);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("itineraries")) || [];
    setItineraries(saved);
  }, []);

  const removeTrip = (index) => {
    if (window.confirm("Are you sure you want to remove this trip?")) {
      const updated = [...itineraries];
      updated.splice(index, 1);
      setItineraries(updated);
      localStorage.setItem("itineraries", JSON.stringify(updated));
    }
  };

  const addMemories = (index) => {
    const memories = prompt(
      "Add your memories or notes for this trip:",
      itineraries[index].memories || ""
    );
    if (memories !== null) {
      const updated = [...itineraries];
      updated[index].memories = memories;
      setItineraries(updated);
      localStorage.setItem("itineraries", JSON.stringify(updated));
    }
  };

  const shareMemories = (memories, tripName) => {
    if (!memories) {
      alert("No memories added yet for this trip.");
      return;
    }

    // Prompt user to choose sharing method
    const method = prompt(
      "Choose share method: email / whatsapp / twitter",
      "email"
    );
    if (!method) return;

    const encodedText = encodeURIComponent(
      `Memories from my trip "${tripName}":\n\n${memories}`
    );

    let url = "";
    switch (method.toLowerCase()) {
      case "email":
        url = `mailto:?subject=My Trip Memories - ${tripName}&body=${encodedText}`;
        window.open(url);
        break;
      case "whatsapp":
        url = `https://wa.me/?text=${encodedText}`;
        window.open(url, "_blank");
        break;
      case "twitter":
        url = `https://twitter.com/intent/tweet?text=${encodedText}`;
        window.open(url, "_blank");
        break;
      default:
        alert("Invalid share method! Choose email, whatsapp, or twitter.");
    }
  };

  return (
    <div className="p-6 bg-white min-h-screen">
      <h1 className="text-2xl font-bold text-blue-600 mb-6">My Trips</h1>

      {itineraries.length === 0 ? (
        <p>No trips yet. Save some itineraries first!</p>
      ) : (
        <ul className="space-y-4 max-w-2xl mx-auto">
          {itineraries.map((item, index) => (
            <li
              key={index}
              className="p-4 border rounded-lg shadow-sm bg-gray-50 flex flex-col sm:flex-row sm:justify-between sm:items-center"
            >
              <div>
                <strong>{item.name}</strong> â€” {item.destination}
                {item.memories && (
                  <p className="mt-2 text-gray-700 italic text-sm">
                    ðŸ’­ Memories: {item.memories}
                  </p>
                )}
              </div>
              <div className="mt-2 sm:mt-0 space-x-2">
                <button
                  onClick={() => addMemories(index)}
                  className="px-2 py-1 bg-green-500 text-white rounded hover:bg-green-600 transition text-sm"
                >
                  Add Memories
                </button>
                <button
                  onClick={() => shareMemories(item.memories, item.name)}
                  className="px-2 py-1 bg-blue-500 text-white rounded hover:bg-blue-600 transition text-sm"
                >
                  Share Memories
                </button>
                <button
                  onClick={() => removeTrip(index)}
                  className="px-2 py-1 bg-red-600 text-white rounded hover:bg-red-700 transition text-sm"
                >
                  Remove
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
