import React, { useEffect, useState } from "react";

export default function MyTripsPage() {
  const [itineraries, setItineraries] = useState([]);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("itineraries")) || [];
    setItineraries(saved);
  }, []);

  const removeTrip = (index) => {
    if (window.confirm("Are you sure you want to remove this trip?")) {
      const updated = [...itineraries];
      updated.splice(index, 1);
      setItineraries(updated);
      localStorage.setItem("itineraries", JSON.stringify(updated));
    }
  };

  const addMemories = async (index) => {
    const trip = itineraries[index];

    // Prompt for note first
    const note = prompt(
      "Add a text note for this trip:",
      trip.memories?.note || ""
    );
    if (note === null) return; // User cancelled -> do nothing

    // Ask if user wants to add files
    const addFiles = window.confirm("Do you want to add images/videos?");
    if (!addFiles) {
      updateTripMemories(index, note, trip.memories?.files || []);
      return;
    }

    // File input for images/videos
    const fileInput = document.createElement("input");
    fileInput.type = "file";
    fileInput.multiple = true;
    fileInput.accept = "image/*,video/*";

    fileInput.onchange = async (e) => {
      const files = Array.from(e.target.files);
      const fileData = [];

      for (const file of files) {
        const base64 = await convertToBase64(file);
        fileData.push({
          name: file.name,
          type: file.type,
          data: base64,
        });
      }

      updateTripMemories(index, note, [...(trip.memories?.files || []), ...fileData]);
    };

    fileInput.click();
  };

  const updateTripMemories = (index, note, files) => {
    const updatedTrip = {
      ...itineraries[index],
      memories: { note, files },
    };
    const updatedItineraries = [...itineraries];
    updatedItineraries[index] = updatedTrip;
    setItineraries(updatedItineraries);
    localStorage.setItem("itineraries", JSON.stringify(updatedItineraries));
  };

  const removeMemoryFile = (tripIndex, fileIndex) => {
    const trip = itineraries[tripIndex];
    const updatedFiles = [...(trip.memories?.files || [])];
    updatedFiles.splice(fileIndex, 1);
    updateTripMemories(tripIndex, trip.memories?.note || "", updatedFiles);
  };

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  const shareMemories = (memories, tripName) => {
    if (!memories) {
      alert("No memories added yet for this trip.");
      return;
    }

    const newWindow = window.open("", "_blank");
    newWindow.document.write(`<h1>Memories from "${tripName}"</h1>`);
    if (memories.note) {
      newWindow.document.write(`<p>${memories.note}</p>`);
    }
    if (memories.files && memories.files.length > 0) {
      memories.files.forEach((file) => {
        if (file.type.startsWith("image/")) {
          newWindow.document.write(
            `<img src="${file.data}" alt="${file.name}" style="max-width: 300px; margin: 10px;"/>`
          );
        } else if (file.type.startsWith("video/")) {
          newWindow.document.write(
            `<video src="${file.data}" controls style="max-width: 300px; margin: 10px;"></video>`
          );
        }
      });
    }

    const encodedText = encodeURIComponent(
      `Memories from my trip "${tripName}":\n${memories.note || ""}`
    );

    const shareLinks = `
      <p>
        Share via: 
        <a href="mailto:?subject=Trip Memories - ${tripName}&body=${encodedText}" target="_blank">Email</a> |
        <a href="https://wa.me/?text=${encodedText}" target="_blank">WhatsApp</a> |
        <a href="https://twitter.com/intent/tweet?text=${encodedText}" target="_blank">Twitter</a>
      </p>
    `;
    newWindow.document.write(shareLinks);
  };

  return (
    <div className="p-6 bg-white min-h-screen">
      <h1 className="text-2xl font-bold text-blue-600 mb-6">My Trips</h1>

      {itineraries.length === 0 ? (
        <p>No trips yet. Save some itineraries first!</p>
      ) : (
        <ul className="space-y-4 max-w-3xl mx-auto">
          {itineraries.map((item, index) => (
            <li
              key={index}
              className="p-4 border rounded-lg shadow-sm bg-gray-50 flex flex-col sm:flex-row sm:justify-between sm:items-start"
            >
              <div className="flex-1">
                <strong>{item.name}</strong> â€” {item.destination}
                {item.memories && (
                  <div className="mt-2 text-gray-700">
                    {item.memories.note && (
                      <p className="italic mb-2">ðŸ’­ Note: {item.memories.note}</p>
                    )}
                    {item.memories.files && item.memories.files.length > 0 && (
                      <div className="grid grid-cols-2 sm:grid-cols-3 gap-2">
                        {item.memories.files.map((file, i) => (
                          <div key={i} className="relative">
                            {file.type.startsWith("image/") ? (
                              <img
                                src={file.data}
                                alt={file.name}
                                className="w-full h-32 object-cover rounded"
                              />
                            ) : (
                              <video
                                src={file.data}
                                controls
                                className="w-full h-32 rounded"
                              />
                            )}
                            <button
                              onClick={() => removeMemoryFile(index, i)}
                              className="absolute top-1 right-1 bg-red-600 text-white rounded-full w-6 h-6 text-xs flex items-center justify-center hover:bg-red-700"
                              title="Remove this file"
                            >
                              âœ•
                            </button>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                )}
              </div>

              <div className="mt-2 sm:mt-0 space-x-2 flex flex-col sm:flex-row">
                <button
                  onClick={() => addMemories(index)}
                  className="px-2 py-1 bg-green-500 text-white rounded hover:bg-green-600 transition text-sm"
                >
                  Add Memories
                </button>
                <button
                  onClick={() => shareMemories(item.memories, item.name)}
                  className="px-2 py-1 bg-blue-500 text-white rounded hover:bg-blue-600 transition text-sm"
                >
                  Share Memories
                </button>
                <button
                  onClick={() => removeTrip(index)}
                  className="px-2 py-1 bg-red-600 text-white rounded hover:bg-red-700 transition text-sm"
                >
                  Remove
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
