import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function SignUpPage() {
  const navigate = useNavigate();
  const [useEmail, setUseEmail] = useState(true);
  const [formData, setFormData] = useState({ name: "", emailOrPhone: "", password: "" });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleFinish = async () => {
    setError("");
    setLoading(true);

    try {
      const payload = {
        name: formData.name,
        password: formData.password,
      };
      if (useEmail) payload.email = formData.emailOrPhone;
      else payload.phone = "+91" + formData.emailOrPhone; // prepend country code

      const res = await fetch("http://localhost:5000/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await res.json();
      if (!res.ok) return setError(data.message || "Server error");

      navigate("/home");
    } catch (err) {
      console.error(err);
      setError("Server error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <input type="text" name="name" placeholder="Full Name" value={formData.name} onChange={handleChange} />
      <input type={useEmail ? "email" : "tel"} name="emailOrPhone" placeholder={useEmail ? "Email" : "Phone"} value={formData.emailOrPhone} onChange={handleChange} maxLength={useEmail ? 50 : 10} />
      <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} />
      <button onClick={handleFinish} disabled={loading}>{loading ? "Creating..." : "Finish"}</button>
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
