import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { User, Mail, Phone, Lock } from "lucide-react";
import { motion } from "framer-motion";

export default function SignUpPage() {
  const navigate = useNavigate();
  const [useEmail, setUseEmail] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    emailOrPhone: "",
    password: "",
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFinish = async () => {
    setError("");
    setLoading(true);

    // Frontend validation
    if (!formData.name.trim()) {
      setError("Please enter your full name");
      setLoading(false);
      return;
    }
    if (useEmail && !formData.emailOrPhone.trim()) {
      setError("Please enter your email");
      setLoading(false);
      return;
    }
    if (!useEmail && formData.emailOrPhone.length !== 13) {
      setError("Please enter a valid 10-digit phone number");
      setLoading(false);
      return;
    }
    if (!formData.password) {
      setError("Please enter a password");
      setLoading(false);
      return;
    }

    try {
      // Prepare payload
      const payload = {
        name: formData.name,
        password: formData.password,
      };

      if (useEmail) payload.email = formData.emailOrPhone;
      else payload.phone = formData.emailOrPhone;

      // Send to backend
      const res = await fetch("http://localhost:5000/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await res.json();

      if (!res.ok) {
        setError(data.message || "Something went wrong");
        setLoading(false);
        return;
      }

      // Navigate to Home page
      navigate("/home");
    } catch (err) {
      console.error("❌ Error in handleFinish:", err);
      setError("Failed to connect to server");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        fontFamily: "Poppins, sans-serif",
        backgroundColor: "#ffffff",
        padding: "20px",
      }}
    >
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        style={{
          maxWidth: "400px",
          width: "100%",
          background: "#fefefe",
          padding: "40px",
          borderRadius: "20px",
          boxShadow: "0px 10px 30px rgba(0,0,0,0.1)",
          textAlign: "center",
        }}
      >
        <h1 style={{ fontSize: "2rem", marginBottom: "10px", color: "#0077ff" }}>
          TravelGo
        </h1>
        <p style={{ fontSize: "1rem", color: "#555", marginBottom: "30px" }}>
          Sign up to Travel with Tripzo ✈️
        </p>

        <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
          {/* Full Name */}
          <div
            style={{
              display: "flex",
              alignItems: "center",
              border: "1px solid #eee",
              borderRadius: "10px",
              padding: "12px",
            }}
          >
            <User style={{ marginRight: "8px", color: "#aaa" }} />
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Full Name"
              style={{ flex: 1, outline: "none", fontSize: "1rem" }}
            />
          </div>

          {/* Email / Phone Toggle */}
          <div style={{ display: "flex", justifyContent: "center", gap: "10px" }}>
            <button
              onClick={() => {
                setUseEmail(true);
                setFormData({ ...formData, emailOrPhone: "" });
              }}
              style={{
                padding: "8px 16px",
                borderRadius: "8px",
                border: "none",
                background: useEmail ? "#0077ff" : "#eee",
                color: useEmail ? "#fff" : "#555",
                cursor: "pointer",
              }}
            >
              Email
            </button>
            <button
              onClick={() => {
                setUseEmail(false);
                setFormData({ ...formData, emailOrPhone: "+91" });
              }}
              style={{
                padding: "8px 16px",
                borderRadius: "8px",
                border: "none",
                background: !useEmail ? "#0077ff" : "#eee",
                color: !useEmail ? "#fff" : "#555",
                cursor: "pointer",
              }}
            >
              Phone
            </button>
          </div>

          {/* Email or Phone Input */}
          <div
            style={{
              display: "flex",
              alignItems: "center",
              border: "1px solid #eee",
              borderRadius: "10px",
              padding: "12px",
            }}
          >
            {useEmail ? (
              <Mail style={{ marginRight: "8px", color: "#aaa" }} />
            ) : (
              <Phone style={{ marginRight: "8px", color: "#aaa" }} />
            )}
            <input
              type={useEmail ? "email" : "tel"}
              name="emailOrPhone"
              value={formData.emailOrPhone}
              onChange={(e) => {
                if (useEmail) {
                  handleChange(e);
                } else {
                  let val = e.target.value;

                  // Remove non-digit characters
                  val = val.replace(/\D/g, "");

                  // Take max 10 digits
                  val = val.slice(0, 10);

                  // Prepend +91
                  setFormData({ ...formData, emailOrPhone: "+91" + val });
                }
              }}
              placeholder={useEmail ? "Email" : "Phone Number"}
              style={{ flex: 1, outline: "none", fontSize: "1rem" }}
            />
          </div>

          {/* Password */}
          <div
            style={{
              display: "flex",
              alignItems: "center",
              border: "1px solid #eee",
              borderRadius: "10px",
              padding: "12px",
            }}
          >
            <Lock style={{ marginRight: "8px", color: "#aaa" }} />
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Create Password"
              style={{ flex: 1, outline: "none", fontSize: "1rem" }}
            />
          </div>
        </div>

        {error && (
          <p style={{ color: "red", fontSize: "0.9rem", marginTop: "10px" }}>
            {error}
          </p>
        )}

        <button
          onClick={handleFinish}
          disabled={loading}
          style={{
            width: "100%",
            marginTop: "30px",
            padding: "12px",
            borderRadius: "12px",
            border: "none",
            background: "linear-gradient(90deg,#0077ff,#00aaff)",
            color: "#fff",
            fontSize: "1rem",
            cursor: "pointer",
            opacity: loading ? 0.7 : 1,
          }}
        >
          {loading ? "Creating account..." : "Finish"}
        </button>

        <p style={{ marginTop: "20px", fontSize: "0.9rem", color: "#555" }}>
          Already have an account?{" "}
          <span
            onClick={() => navigate("/signin")}
            style={{ color: "#0077ff", cursor: "pointer" }}
          >
            Sign In
          </span>
        </p>
      </motion.div>
    </div>
  );
}
