import React, { useState } from "react";

export default function AIPlanner() {
  const [form, setForm] = useState({
    location: "",
    interests: "",
    days: "",
    budget: "",
  });
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await fetch("http://localhost:5001/api/ai/recommendations", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      setResult(data.itinerary);
    } catch (error) {
      console.error("Error fetching itinerary:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-white text-gray-800 flex flex-col items-center p-6">
      <h1 className="text-blue-600 text-2xl sm:text-3xl font-bold mb-6">
        AI Trip Planner
      </h1>

      <form
        onSubmit={handleSubmit}
        className="bg-gray-50 w-full max-w-md p-6 rounded-2xl shadow-lg"
      >
        {["location", "interests", "days", "budget"].map((field) => (
          <input
            key={field}
            name={field}
            placeholder={`Enter ${field}`}
            value={form[field]}
            onChange={handleChange}
            className="w-full mb-3 px-4 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
        ))}

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition"
          disabled={loading}
        >
          {loading ? "Planning..." : "Get Itinerary"}
        </button>
      </form>

      {result && (
        <div className="mt-6 w-full max-w-md bg-white p-5 border rounded-xl shadow">
          <h2 className="font-semibold text-gray-800 mb-3">Your Itinerary</h2>
          <ul className="space-y-4">
            {result.map((day, i) => (
              <li
                key={i}
                className="border rounded-lg bg-gray-50 p-3 text-sm shadow-sm"
              >
                <strong>{day.day}</strong>
                <ul className="list-disc list-inside text-gray-700 mt-2">
                  {day.activities.map((act, j) => (
                    <li key={j}>{act}</li>
                  ))}
                </ul>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
