import React, { useState } from "react";
import axios from "axios";

export default function AIPlanner() {
  const [destination, setDestination] = useState("");
  const [interest, setInterest] = useState("");
  const [days, setDays] = useState("");
  const [budget, setBudget] = useState("");
  const [recommendations, setRecommendations] = useState(null);

  const getRecommendations = async () => {
    try {
      const response = await axios.post("http://localhost:5001/api/ai/recommendations", {
        destination,
        interest,
        days,
        budget,
      });
      setRecommendations(response.data);
    } catch (error) {
      console.error("Error fetching recommendations:", error);
      alert("Failed to fetch recommendations.");
    }
  };

  const saveItinerary = () => {
    const itineraryName = prompt("Enter a name for this itinerary:");
    if (!itineraryName) return;

    const newItinerary = {
      name: itineraryName,
      destination,
      interest,
      days,
      budget,
      recommendations,
      createdAt: new Date().toISOString(),
    };

    const savedItineraries = JSON.parse(localStorage.getItem("itineraries")) || [];
    savedItineraries.push(newItinerary);
    localStorage.setItem("itineraries", JSON.stringify(savedItineraries));

    alert(`Itinerary "${itineraryName}" saved successfully!`);
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-semibold mb-4 text-blue-600">AI Trip Planner</h1>

      <div className="flex flex-col gap-2 max-w-md">
        <input
          type="text"
          placeholder="Destination"
          className="border p-2 rounded"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
        />
        <input
          type="text"
          placeholder="Interest (e.g. beaches, parties)"
          className="border p-2 rounded"
          value={interest}
          onChange={(e) => setInterest(e.target.value)}
        />
        <input
          type="number"
          placeholder="Days"
          className="border p-2 rounded"
          value={days}
          onChange={(e) => setDays(e.target.value)}
        />
        <input
          type="number"
          placeholder="Budget"
          className="border p-2 rounded"
          value={budget}
          onChange={(e) => setBudget(e.target.value)}
        />
        <button
          onClick={getRecommendations}
          className="bg-blue-600 text-white py-2 rounded mt-2"
        >
          Get Recommendations
        </button>
      </div>

      {recommendations && (
        <div className="mt-5 bg-gray-50 p-4 rounded shadow">
          <h2 className="text-lg font-semibold">Recommendations</h2>
          <p>{recommendations.recommendation}</p>
          <p className="text-sm text-gray-500">Best Time: {recommendations.bestTime}</p>
          <button
            onClick={saveItinerary}
            className="mt-3 bg-green-600 text-white py-1 px-4 rounded"
          >
            ðŸ’¾ Save Itinerary
          </button>
        </div>
      )}
    </div>
  );
}
