import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Search, Bell, User } from "lucide-react";
import IconCard from "../components/IconCard";

const features = [
  { name: "AI Planner", icon: "ü§ñ", path: "/ai-recommendation" },
  { name: "Itinerary", icon: "üóìÔ∏è", path: "/itinerary" },
  { name: "My Trips", icon: "üß≥", path: "/my-trips" },
  { name: "Translator", icon: "üí¨", path: "/translator" },
  { name: "Expenses", icon: "üíµ", path: "/expenses" },
  { name: "Memories", icon: "üì∏", path: "/memories" },
  { name: "City Explorer", icon: "üåÜ", path: "/city-explorer" },
  { name: "Community", icon: "üë•", path: "/community" },
  { name: "Premium", icon: "üíé", path: "/premium" },
  { name: "Booking", icon: "üè®", path: "/booking" },
];

export default function Home() {
  const navigate = useNavigate();
  const [showMenu, setShowMenu] = useState(false);

  return (
    <div className="min-h-screen bg-white text-gray-900 flex flex-col">
      {/* Navbar */}
      <header className="sticky top-0 z-10 bg-white shadow-sm border-b flex justify-between items-center px-5 py-3">
        <h1 className="text-lg sm:text-xl font-semibold text-blue-600 tracking-wide">
          Travveler
        </h1>
        <div className="flex items-center gap-4 relative">
          <Bell className="text-blue-600 cursor-pointer" size={22} />
          <div className="relative">
            <User
              className="text-blue-600 cursor-pointer"
              size={24}
              onClick={() => setShowMenu(!showMenu)}
            />
            {showMenu && (
              <div className="absolute right-0 mt-3 w-48 bg-white border shadow-lg rounded-xl p-3 text-sm">
                <p className="font-semibold">Saranya</p>
                <p className="text-gray-500 text-xs mb-2">saranya@example.com</p>
                <hr className="my-2" />
                <button className="block w-full text-left py-1 hover:text-blue-600">
                  ‚≠ê Get Premium
                </button>
                <button className="block w-full text-left py-1 hover:text-red-500">
                  ‚ùå Cancel Premium
                </button>
                <button className="block w-full text-left py-1 hover:text-blue-600">
                  üîì Logout
                </button>
              </div>
            )}
          </div>
        </div>
      </header>

      {/* Search Bar */}
      <div className="p-4">
        <div className="bg-gray-100 flex items-center px-4 py-3 rounded-full shadow-sm max-w-xl mx-auto w-full">
          <Search className="text-gray-400 mr-3" />
          <input
            type="text"
            placeholder="Search for destinations, trips or experiences..."
            className="flex-1 outline-none bg-transparent text-gray-800 text-sm sm:text-base"
          />
        </div>
      </div>

      {/* Feature Grid */}
      <div className="grid grid-cols-3 sm:grid-cols-4 md:grid-cols-5 gap-5 p-5 sm:p-8 justify-items-center">
        {features.map((f) => (
          <IconCard
            key={f.name}
            icon={f.icon}
            label={f.name}
            onClick={() => navigate(f.path)}
          />
        ))}
      </div>
    </div>
  );
}
